    private void pSobreMouseDragged(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_pSobreMouseDragged
        int x = evt.getXOnScreen();
        int y = evt.getYOnScreen();
        
        this.setLocation(x-xx,y-yy);
    }//GEN-LAST:event_pSobreMouseDragged

