    private void finalizar(){
        int prueba;
        try{
            for(int i = 0; i < identificadores.length; i++){
                prueba = Integer.parseInt(identificadores[0]);
            }
            contador = 1;
            mCorrecto.setVisible(true);
            
            
        }catch( NumberFormatException e){
            mError.setVisible(true);
            contador = 1;
        }
    }
